atrix(~themeGroup-1, data=data)\n","encoded_availability <- model.matrix(~availability-1, data=data)\n","\n","# Scale numeric variables\n","scaled_numeric <- scale(data %>% select(pieces,minifigs, return, time_available, num_reviews, overall, min_retail_price ))\n","# Combine scaled numeric variables with encoded categorical variables\n","encoded_data <- cbind(scaled_numeric, encoded_theme, encoded_themeGroup,\n","                       encoded_availability)\n","\n","dim(encoded_data)"],"metadata":{"id":"HVGk2aQbHYO4","colab":{"base_uri":"https://localhost:8080/","height":1000},"executionInfo":{"status":"error","timestamp":1729221591436,"user_tz":-780,"elapsed":43,"user":{"displayName":"Ethan Wijaya","userId":"05432600493302357628"}},"outputId":"8312d519-3dbb-4682-b4ce-317d00fa5abf"},"execution_count":2,"outputs":[{"output_type":"error","ename":"ERROR","evalue":"Error: 'ppm_data_nathan.csv' does not exist in current working directory ('/content').\n","traceback":["Error: 'ppm_data_nathan.csv' does not exist in current working directory ('/content').\nTraceback:\n","1. vroom::vroom(file, delim = \",\", col_names = col_names, col_types = col_types, \n .     col_select = {\n .         {\n .             col_select\n .         }\n .     }, id = id, .name_repair = name_repair, skip = skip, n_max = n_max, \n .     na = na, quote = quote, comment = comment, skip_empty_rows = skip_empty_rows, \n .     trim_ws = trim_ws, escape_double = TRUE, escape_backslash = FALSE, \n .     locale = locale, guess_max = guess_max, show_col_types = show_col_types, \n .     progress = progress, altrep = lazy, num_threads = num_threads)","2. vroom_(file, delim = delim %||% col_types$delim, col_names = col_names, \n .     col_types = col_types, id = id, skip = skip, col_select = col_select, \n .     name_repair = .name_repair, na = na, quote = quote, trim_ws = trim_ws, \n .     escape_double = escape_double, escape_backslash = escape_backslash, \n .     comment = comment, skip_empty_rows = skip