 +\n","    geom_point(alpha = 0.5) +\n","    labs(title = \"Mean NZD New vs. overall by Cluster\",\n","         x = \"overall\",\n","         y = \"Mean NZD New\") +\n","    theme_minimal()"],"metadata":{"id":"2SFdAHrMSxFb"},"execution_count":null,"outputs":[]},{"cell_type":"code","source":["#custom cluster names\n","  cluster_names <- c(\"Outliers\" = \"0\",\n","                     \"Retail\" = \"1\",\n","                     \"Non-retail Star Wars\" = \"2\",\n","                     \"Retail Star Wars\" = \"3\",\n","                     \"Model making\" = \"4\",\n","                     \"High returns\" = \"5\",\n","                     \"Lowest returns\" = \"6\")\n","\n","  # Plot with renamed clusters\n","  ggplot(pca_data, aes(x = PC1, y = PC2, color = as.factor(cluster))) +\n","    geom_point() +\n","    labs(title = \"DBSCAN Cluster Plot (epsilon = 0.7, minPts = 6)\",\n","         x = \"Principal Component 1\",\n","         y = \"Principal Component 2\",\n","         color = \"Clusters\") +\n","    scale_color_manual(values = rainbow(length(cluster_names)),\n","                       labels = names(cluster_names)) +\n","    theme_minimal() +\n","    theme(legend.position = \"right\")\n","\n","  #cluster numbers to names\n","  cluster_names <- c(\"0\" = \"Outliers\" ,\n","                     \"1\" = \"Retail\",\n","                     \"2\" = \"Non-retail Star Wars\",\n","                     \"3\" = \"Retail Star Wars\",\n","                     \"4\" = \"Model making\",\n","                     \"5\" = \"High returns\",\n","                     \"6\" = \"Lowest returns\")\n","\n","\n","  # Create the boxplot\n","  ggplot(pca_data, aes(x = as.factor(cluster), y = return)) +\n","    geom_boxplot(fill =\"#4f6980\", alpha = 0.7) +\n","    scale_x_discrete(labels = cluster_names) +\n","    labs(title = \"Distribution of Returns by Cluster\",\n","         x = \"Cluster\",\n","         y = \"Returns (%)\") +\n","    theme_minimal() +\n","    theme(\n","      plo